package com.example.numbermaster

import org.junit.Test

import org.junit.Assert.*

/**
 * Example local unit test, which will execute on the development machine (host).
 *
 * See [testing documentation](http://d.android.com/tools/testing).
 */
class NumberMasterUtilUnitTest {
    private val numbersOk = listOf(
        "8160000003570000004 ",
        "8168160003573570004 ",
        "8168168163573573574 ",
        )

    private val numbersNg = listOf(
        // 9x3
        "8168168163573573574 ",
        // 9x6
        "8168168163573573574 ",
        // 6x3
        "8168160003573570004 ",
        // non numberが無い
        "",
    )

    @Test
    fun addition_isCorrect() {
        this.validateUnitTest()
    }

    private fun validateUnitTest() {
        for (numbers in this.numbersOk) {
            assertTrue(Util.validate(numbers))
        }

        for (numbers in this.numbersNg) {
            assertFalse(Util.validate(numbers))
        }
    }
}